"use strict";
jQuery(document).ready(function () {
    jQuery(".content-slider").each(function theSlider(e, i) {
        var t = "adaptive";
        jQuery(i).attr("id", t + e),
            jQuery("#" + t + e).lightSlider({
                item: 4,
                slideMove: 1,
                slideMargin: 15,
                keyPress: !0,
                pager: !1,
                gallery: !0,
                responsive: [
                    { breakpoint: 1260, settings: { item: 4, slideMove: 1, slideMargin: 12 } },
                    { breakpoint: 1118, settings: { item: 3, slideMove: 1, slideMargin: 10 } },
                    { breakpoint: 1006, settings: { item: 2, slideMove: 1, slideMargin: 15 } },
                    { breakpoint: 700, settings: { item: 2, slideMove: 1, slideMargin: 10 } },
                    { breakpoint: 520, settings: { item: 1, slideMove: 1, slideMargin: 0 } },
                ],
            });
    });
    function initAufhaenungSlider() {
        var resizeTimer;
        if ($(window).width() > 768) {
            initTeaserHeight();
            $("#" + t + e).parent().parent().find('.lSAction, .lSPager').remove();
            $('content-slider').children().removeAttr('style');
        } else {
            theSlider();
            $('.content-slider .col-item').css('width', '100%');
        }
    }
}),
    jQuery(document).ready(function () {
        jQuery(window).scroll(function () {
            jQuery(this).scrollTop() > 100 ? jQuery(".scroll-top").fadeIn() : jQuery(".scroll-top").fadeOut();
        }),
            jQuery(".scroll-top").click(function () {
                return jQuery("html, body").animate({ scrollTop: 0 }, 600), !1;
            });
    });